import React from "react";
import styled from "styled-components";
import { tablet } from "../Responsive";

const Wrapper = styled.div`
  margin: 0;
  padding: 10px;
  min-height: 50vh;

  .content {
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    ${tablet({ flexDirection: "row" })}
  }
  .cart {
    flex: 100%;
    display: grid;
    grid-template-columns: 20% 30% 15% 20% 15%;
    ${tablet({ flex: "70%" })}
  }
  .summary {
    flex: 100%;
    ${tablet({ flex: "30%" })}
  }
  .img-container {
    height: 60px;
    width: 60px;
  }
  img {
    height: 100%;
    width: 100%;
  }
`;

function Cart(props) {
  // const productId = props.match.params.id;
  // const qty = props.location.search ? Number(props.location.split("=")[1]) : 1;

  return (
    <Wrapper>
      <h1>Cart screen</h1>
      {/* <p>
          ADD TO CART: productID: {productId} Qty: {qty}
        </p> */}
      <div className="content">
        <div className="cart">
          <div className="img-container">
            <img src="/items/main.jpg" alt="product icon" />
          </div>
          <p className="name">Name of item</p>
          <input type="number" />
          <p className="price"></p>
          <button className="btn btn-secondary">Delete</button>
        </div>
        <div className="summary">
          <p className="total">
            Subtotal(9 items): $<span>180</span>
          </p>
          <button className="btn btn-info">Proceed to Checkout</button>
        </div>
      </div>
    </Wrapper>
  );
}

export default Cart;
