import axios from "axios";
import {
  PRODUCT_LIST_FAIL,
  PRODUCT_LIST_REQUUEST,
  PRODUCT_LIST_SUCCESS,
} from "./actionType";

export const addToCart = (product) => {
  return {
    type: ADDPRODUCT,
    payload: product,
  };
};

export const updateCart = (product) => {
  return {
    type: UPDATEPRODUCT,
    payload: product,
  };
};

export const deleteCart = (product) => {
  return {
    type: DELETEPRODUCT,
    payload: product,
  };
};
