import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";

const Wrapper = styled.div`
  display: grid;
  grid-template-columns: 50% 50%;
`;

function Product() {
  const [product, setProduct] = useState({});
  const { id } = useParams();

  const getProduct = async () => {
    try {
      const resp = await fetch(`http://localhost:3000/product/${id}`);
      const data = await resp.json();
      setProduct(data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getProduct();
  }, [product]);

  return (
    <Wrapper className="container">
      <div className="img-container">
        <img
          src="/items/project1.jpg"
          alt="product"
          height="300px"
          width="200px"
        />
      </div>
      <div className="content">
        <div className="desc">
          <h2>Addidas Fit Shirt</h2>
          <span>
            <i
              className={
                rating >= 1
                  ? "fa fa-star"
                  : rating >= 0.5
                  ? "fa fa-star-half-stroke"
                  : "fa-regular fa-star"
              }
            ></i>
          </span>
          <span>
            <i
              className={
                rating >= 1
                  ? "fa fa-star"
                  : rating >= 0.5
                  ? "fa fa-star-half-stroke"
                  : "fa-regular fa-star"
              }
            ></i>
          </span>
          <span>
            <i
              className={
                rating >= 1
                  ? "fa fa-star"
                  : rating >= 0.5
                  ? "fa fa-star-half-stroke"
                  : "fa-regular fa-star"
              }
            ></i>
          </span>
          <span>
            <i
              className={
                rating >= 1
                  ? "fa fa-star"
                  : rating >= 0.5
                  ? "fa fa-star-half-stroke"
                  : "fa-regular fa-star"
              }
            ></i>
          </span>
          <span>
            <i
              className={
                rating >= 1
                  ? "fa fa-star"
                  : rating >= 0.5
                  ? "fa fa-star-half-stroke"
                  : "fa-regular fa-star"
              }
            ></i>
          </span>
        </div>
        <div className="addtocart"></div>
      </div>
    </Wrapper>
  );
}

export default Product;
