import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { NavLink, Link } from "react-router-dom";
import styled from "styled-components";
import { addToCart, deleteCart } from "../redux/action";

const Wrapper = styled.div`
  .content {
    display: flex;
    flex-wrap: wrap;
  }
  .cart {
    flex: 100;
    display: grid;
    grid-template-columns: 15% 35% 15% 15% 20%;
  }
  input {
    width: 40px;
  }
  .total {
    background-color: gray;
  }
  .checkout {
    background-color: yellow;
  }
`;

function Cart() {
  const cart = useSelector((state) => state.product);

  const dispatch = useDispatch();

  const addProduct = (product) => dispatch(addToCart(product));
  const deleteProduct = (product) => dispatch(deleteCart(product));
  return (
    <Wrapper>
      <h3>Shopping Cart</h3>
      <div className="content">
        <div className="cart">
          <img
            src="/items/project1.jpg"
            alt="product cover"
            height="40"
            width="40"
          />
          <h4>product name</h4>
          <input type="number" />
          <p className="price">Price</p>
          <button>Delete</button>
        </div>
        <div className="total">
          <p>
            Subtotal
            <scan>
              (9 items) : $<scan></scan>18
            </scan>
          </p>
          <button className="checkout">Proceed to Checkout</button>
        </div>
      </div>
    </Wrapper>
  );
}

export default Cart;
