import React from "react";
import styled from "styled-components";
import { tablet } from "../Responsive";

const Wrapper = styled.div`
  margin: 0;
  padding: 10px;
  min-height: 50vh;

  .content {
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    ${tablet({ flexDirection: "row" })}
  }
  .cart {
    flex: 100%;
    ${tablet({ flex: "70%" })}
  }
  .cart-items {
    display: grid;
    grid-template-columns: 15% 25% 15% 15% 20%;
    place-content: center;
  }
  .summary {
    flex: 100%;
    background-color: aliceblue;
    padding: 20px;
    ${tablet({ flex: "30%", alignSelf: "center" })}
  }
  .img-container {
    height: 50px;
    width: 50px;
  }
  img {
    height: 100%;
    width: 100%;
    border: 2px solid black;
    border-radius: 10px;
  }
  .del {
    height: 40px;
    margin-right: 20px;
  }
  input {
    width: 50px;
    height: 40px;
  }
  .cart p {
    align-self: center;
  }
`;

function Cart(props) {
  // const productId = props.match.params.id;
  // const qty = props.location.search ? Number(props.location.split("=")[1]) : 1;

  return (
    <Wrapper>
      <h1>Cart screen</h1>
      {/* <p>
          ADD TO CART: productID: {productId} Qty: {qty}
        </p> */}
      <div className="content">
        <div className="cart">
          <div className="cart-items">
            <div className="img-container">
              <img src="/items/main.jpg" alt="product icon" />
            </div>
            <p className="name">Name of item</p>
            <input type="number" />
            <p className="price">$904</p>
            <button className="btn btn-secondary del">Delete</button>
          </div>
        </div>
        <div className="summary">
          <p className="total">
            Subtotal(9 items): $<span>180</span>
          </p>
          <button className="btn btn-info">Proceed to Checkout</button>
        </div>
      </div>
    </Wrapper>
  );
}

export default Cart;
