import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import Rating from "./Rating";
import { tablet } from "../Responsive";
import { Data } from "../configs/data";

const Wrapper = styled.div`
  display: grid;
  grid-template-columns: 100%;
  gap: 10px;
  margin: 1rem;
  ${tablet({ gridTemplateColumns: "50% 50%" })}

  .content {
    display: flex;
    align-items end;
    ${tablet({ flexDirection: "row" })}
  }

  .desc {
    ${tablet({ flex: "50%" })}
  }

  .addtocart {
    ${tablet({ flex: "50%" })}
  }
`;

function Product() {
  const { id } = useParams();
  //const [product, setProduct] = useState({});

  // const getProduct = async () => {
  //   try {
  //     const resp = await fetch(`http://localhost:3000/product/${id}`);
  //     const data = await resp.json();
  //     setProduct(data);
  //     console.log(data);
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };

  // useEffect(() => {
  //   getProduct();
  // }, []);

  const product = Data.find((item, i) => item._id === id);

  return (
    <Wrapper>
      <div className="img-container">
        <img
          src="/items/project1.jpg"
          alt="product"
          height="300px"
          width="200px"
        />
      </div>
      <div className="content">
        <div className="desc">
          <h3>Addidas Fit Shirt</h3>
          <Rating rating={product.rating} numReviews={product.numReviews} />
          <div className="price">
            <h5>Price: ${product.price}</h5>
            <p>Description: {product.description}</p>
          </div>
        </div>
        <div className="addtocart">
          <h4>Price {product.price}</h4>
          <h4>
            Status{" "}
            <p>
              {product.countInStock > 0 ? (
                <span className="success">In Stock</span>
              ) : (
                <span className="danger error">Unavailable</span>
              )}
            </p>
          </h4>
          <button>Add to Cart</button>
        </div>
      </div>
    </Wrapper>
  );
}

export default Product;
