import { ADDPRODUCT, DELETEPRODUCT, UPDATEPRODUCT } from "./actionType";

export const addProduct = (product) => {
  return {
    type: ADDPRODUCT,
    payload: product,
  };
};

export const updateProduct = (product) => {
  return {
    type: UPDATEPRODUCT,
    payload: product,
  };
};

export const deleteProduct = (product) => {
  return {
    type: DELETEPRODUCT,
    payload: product,
  };
};
