import React from "react";
//import { useDispatch, useSelector } from "react-redux";
//import { NavLink, Link } from "react-router-dom";
import styled from "styled-components";
import { tablet } from "../Responsive";

const Wrapper = styled.div`
  padding: 10px;
  min-height: 70vh;

  .content {
    display: flex;
    flex-wrap: wrap;
  }
  .main {
    flex: 100%;
    margin: 20px;
    margin-top: 0;
    margin-left: 0;
    ${tablet({ flex: "65%" })}
  }
  .cart {
    display: grid;
    grid-template-columns: 15% 30% 15% 15% 20%;
    gap: 3px;
    margin: 5px;
  }
  .name {
    color: darkblue;
  }
  input {
    width: 50px;
    height: 30px;
    border-radius: 5px;
  }
  .deletecart {
    height: 30px;
    border-radius: 5px;
    width: 80px;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  .total {
    display: flex;
    flex-direction: column;
    align-items: center;
    max-height: 120px;
    flex: 100%;
    background-color: gray;
    padding-top: 20px;
    ${tablet({ flex: "30%" })}
  }
  .checkout {
    background-color: yellow;
    border-radius: 5px;
  }
`;

function Cart(props) {
  const productId = props.match.params.id;
  const qty = props.location.search ? Number(props.location.split("=")[1]) : 1;

  return (
    <Wrapper>
      <div className="content">
        <h1>Cart screen</h1>
        <p></p>
      </div>
    </Wrapper>
  );
}

export default Cart;
