import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { NavLink, Link } from "react-router-dom";
import styled from "styled-components";
import { addToCart, deleteCart } from "../redux/action";

const Wrapper = styled.div`
  .cart {
    display: grid;
    grid-template-columns: 15% 35% 15% 15% 20%;
  }
  input {
    width: 40px;
  }
`;

function Cart() {
  const cart = useSelector((state) => state.product);

  const dispatch = useDispatch();

  const addProduct = (product) => dispatch(addToCart(product));
  const deleteProduct = (product) => dispatch(deleteCart(product));
  return (
    <Wrapper>
      <h3>Shopping Cart</h3>
      <div className="cart">
        <img
          src="/items/project1.jpg"
          alt="product cover"
          height="40"
          width="40"
        />
        <h4>product name</h4>
        <input type="number" />
        <p className="price">Price</p>
        <button>Delete</button>
      </div>
    </Wrapper>
  );
}

export default Cart;
