import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { NavLink, Link } from "react-router-dom";
import styled from "styled-components";
import { addToCart, deleteCart } from "../redux/action";
import { tablet } from "../Responsive";

const Wrapper = styled.div`
  padding: 10px;
  min-height: 70vh;

  .content {
    display: flex;
    flex-wrap: wrap;
  }
  .main {
    flex: 100%;
    margin: 20px;
    margin-top: 0;
    ${tablet({ flex: "65%" })}
  }
  .cart {
    display: grid;
    grid-template-columns: 15% 35% 15% 15% 20%;
  }
  input {
    width: 50px;
    height: 30px;
    border-radius: 5px;
  }
  .deletecart {
    height: 30px;
    border-radius: 5px;
  }
  .total {
    align-self: center;
    flex: 100%;
    background-color: gray;
    padding: 20px;
    ${tablet({ flex: "30%" })}
  }
  .checkout {
    background-color: yellow;
  }
`;

function Cart() {
  const cart = useSelector((state) => state.product);

  const dispatch = useDispatch();

  const addProduct = (product) => dispatch(addToCart(product));
  const deleteProduct = (product) => dispatch(deleteCart(product));
  return (
    <Wrapper>
      <div className="content">
        <div className="main">
          <h3 className="mb-3">Shopping Cart</h3>
          <div className="cart">
            <img
              src="/items/project1.jpg"
              alt="product cover"
              height="40"
              width="40"
            />
            <h4>product name</h4>
            <input type="number" />
            <p className="price">Price</p>
            <button className="deletecart">Delete</button>
          </div>
        </div>
        <div className="total">
          <p>
            Subtotal
            <scan>
              (9 items) : $<scan></scan>18
            </scan>
          </p>
          <button className="checkout">Proceed to Checkout</button>
        </div>
      </div>
    </Wrapper>
  );
}

export default Cart;
