import { ADDPRODUCT, DELETEPRODUCT, UPDATEPRODUCT } from "./actionType";

export const addToCart = (product) => {
  return {
    type: ADDPRODUCT,
    payload: product,
  };
};

export const updateCart = (product) => {
  return {
    type: UPDATEPRODUCT,
    payload: product,
  };
};

export const deleteCart = (product) => {
  return {
    type: DELETEPRODUCT,
    payload: product,
  };
};
