import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import Rating from "./Rating";
import { tablet } from "../Responsive";
import { Data } from "../configs/data";

const Wrapper = styled.div`
  display: grid;
  grid-template-columns: 50% 50%;
  grid-gap: 10px;
  ${tablet({ gridTemplateColumns: "100%" })}
`;

function Product() {
  const { id } = useParams();
  //const [product, setProduct] = useState({});

  // const getProduct = async () => {
  //   try {
  //     const resp = await fetch(`http://localhost:3000/product/${id}`);
  //     const data = await resp.json();
  //     setProduct(data);
  //     console.log(data);
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };

  // useEffect(() => {
  //   getProduct();
  // }, []);

  const product = Data.find((item, i) => item._id === id);

  return (
    <Wrapper className="container">
      <div className="img-container">
        <img
          src="/items/project1.jpg"
          alt="product"
          height="300px"
          width="200px"
        />
      </div>
      <div className="content">
        <div className="desc">
          <h2>Addidas Fit Shirt</h2>
          <Rating rating={product.rating} numReviews={product.numReviews} />
          <div className="price">
            <h4>Price: ${product.price}</h4>
            <h4>Description: {product.description}</h4>
          </div>
        </div>
        <div className="addtocart">
          <h4>Price {product.price}</h4>
          <h4>
            Status{" "}
            <p>
              {product.countInStock > 0 ? (
                <span className="success">In Stock</span>
              ) : (
                <span className="danger error">Unavailable</span>
              )}
            </p>
          </h4>
          <button>Add to Cart</button>
        </div>
      </div>
    </Wrapper>
  );
}

export default Product;
