import React from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";
import { tablet } from "../Responsive";

const Wrapper = styled.div`
  display: grid;
  grid-template-columns: 25% 25% 25% 25%;
  padding: 5px;
  gap: 10px;
  ${tablet({ flex: "25%", justifyContent: "center" })}

  .item {
    justify-content: center;
  }
  .card {
    border: 0.1rem #c0c0c0 solid;
    background-color: #f8f8f8;
    border-radius: 0.5rem;
    margin: 1rem;
  }
  img {
    border-radius: 0.5rem;
    max-width: 29rem;
    width: 100%;
    height: 800px;
  }
  h1 {
    font-size: 1.8rem;
    padding: 1rem 0;
  }
  h2 {
    font-size: 1.6rem;
    padding: 1rem 0;
  }
  .rating span {
    color: #f0c040;
    margin: 0.1rem;
  }
  .rating span:last-child {
    color: #404040;
  }
`;

function Home() {
  return (
    <Wrapper className="row">
      <div className="card">
        <NavLink to="/singleproduct">
          <img src="/items/project1.jpg" alt="product" />
          <div className="card-body">
            <h1>Mike Slim shirt</h1>
          </div>
        </NavLink>
        <div className="rating">
          <span>
            <i className="fa fa-star"></i>
          </span>
          <span>
            <i className="fa fa-star"></i>
          </span>
          <span>
            <i className="fa fa-star"></i>
          </span>
          <span>
            <i className="fa fa-star-half-stroke"></i>
          </span>
          <span>
            <i className="fa-regular fa-star"></i>
          </span>
        </div>
        <div className="price">
          <h2>$20.24</h2>
        </div>
      </div>
    </Wrapper>
  );
}

export default Home;
